const path = require("path");
const fs = require("fs");
const { execSync } = require("child_process");

const specDir = path.join(process.cwd(), "spec");
const execOpt = { stdio: "inherit", cwd: specDir };
const outFile = "./index.d.ts";
const json2tsCommand = `json2ts -i contract-object.spec.json -o ${outFile} --no-bannerComment`;
const banner = `/* tslint:disable */

/**
 * This file was automatically generated by json-schema-to-typescript.
 * DO NOT MODIFY IT BY HAND. Instead, modify the source JSONSchema file,
 * and run json-schema-to-typescript to regenerate this file.
 */

`;

//generate file
execSync(json2tsCommand, execOpt); 
//then add banner
const targetFile = path.join(specDir, outFile);
const generated = fs.readFileSync(targetFile);
modified = banner + generated.toString();
fs.writeFileSync(targetFile, modified);